mapper.select:
select ui.realname,ui.nickname,a.id,a.price,a.quantity,ui.uid,a.create_time,a.state from user_trade a left join user_info ui on a.uid = ui.uid
1 = 1
and a.uid <> #{uid}
and a.state = '1'
order by price ,create_time limit #{pageNum},#{pageTotal}

select a.id,a.price,a.quantity,b.img_type as imgType, b.img_url as imgUrl,c.realname,c.mobile from user_trade a left join user_pay_url b on a.uid = b.uid left join user_info c on a.uid = c.uid
1 = 1
and a.id = #{id}
and b.uid = #{uid}
order by img_type asc

select distinct (select b.nickname from user_info b where a.uid = b.uid) nickname, (select b.mobile from user_info b where a.uid = b.uid) mobile, a.id, a.price, a.quantity, a.uid, a.create_time, tp.poundage, (select b.mobile from user_info b where b.uid = uth.purchase_uid) purchaseMobile, (select b.nickname from user_info b where b.uid = uth.purchase_uid) purchaseName from user_trade a left join trade_poundage tp on tp.user_trade_id = a.id left join user_trade_history uth on a.id = uth.user_trade_id
1 = 1
and a.id = #{id}
and a.state = #{state}

select a.price, a.quantity, tp.poundage from user_trade a left join trade_poundage tp on tp.user_trade_id = a.id
1 = 1
and a.id = #{id}
and a.state = #{state}

select b.realname,a.id,a.price,a.quantity,a.uid,a.create_time,tp.poundage,b.mobile from user_trade a left join trade_poundage tp on tp.user_trade_id = a.id left join user_info b on a.uid = b.uid
1 = 1
and a.id = #{id}
and a.state = #{state}

select count(*) from (
    select uth.user_trade_id as orderId,uth.price as price,uth.sell_quantity as quantity,uth.finish_time as finishTime,'0' tradeState,uth.state,ui.nickname from user_trade_history uth
    left join user_info ui on ui.uid = uth.sell_uid where state = '4' and uth.sell_uid = #{uid} or purchase_uid = #{uid}
    UNION
    select ubh.user_buy_id as orderId, ubh.price,ubh.quantity,ubh.finish_time as finishTime,'1' tradeState,ui.nickname,ubh.sell_state as state from user_buy_history ubh
    left join user_info ui on ui.uid = ubh.sell_uid
    where 1 = 1 and sell_state = '6' and ubh.sell_uid = #{uid} OR purchase_uid = #{uid}
    ) t
